SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_motivo_cancel_agenda (nr_seq_agenda_p bigint, cd_motivo_cancel_p text) RETURNS varchar AS $body$
DECLARE


ds_motivo_obs_w	varchar(255);
ds_motivo_dom_w	varchar(255);
ds_motivo_w		varchar(4000);
ds_responsavel_w	varchar(255);
cd_tipo_agenda_w	bigint;


BEGIN
if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then
	/* obter motivo observacao */

	select	max(ds_motivo_status),
		'(' || max(nm_usuario_cancel) ||  chr(32) || wheb_mensagem_pck.get_texto(456028) || chr(32) ||PKG_DATE_FORMATERS.TO_VARCHAR(max(dt_cancelamento), 'timestamp', WHEB_USUARIO_PCK.GET_CD_ESTABELECIMENTO, WHEB_USUARIO_PCK.GET_NM_USUARIO) || ')',
		max(obter_tipo_agenda(cd_agenda))
	into STRICT	ds_motivo_obs_w,
		ds_responsavel_w,
		cd_tipo_agenda_w
	from	agenda_paciente
	where	nr_sequencia = nr_seq_agenda_p;

	if (cd_tipo_agenda_w = 1) then
		select	max(ds_motivo)
		into STRICT	ds_motivo_dom_w
		from	agenda_motivo_cancelamento
		where	cd_motivo = cd_motivo_cancel_p
		and	ie_agenda in ('CI','T');
	else	
		select	max(ds_motivo)
		into STRICT	ds_motivo_dom_w
		from	agenda_motivo_cancelamento
		where	cd_motivo = cd_motivo_cancel_p;
	end if;	

	if (ds_motivo_obs_w IS NOT NULL AND ds_motivo_obs_w::text <> '') and (ds_motivo_dom_w IS NOT NULL AND ds_motivo_dom_w::text <> '') then
		ds_motivo_w := substr(ds_motivo_obs_w || ' - ' || ds_motivo_dom_w,1,255) || ' ' || ds_responsavel_w;
	else
		ds_motivo_w := substr(coalesce(ds_motivo_obs_w, ds_motivo_dom_w),1,255) || ' ' || ds_responsavel_w;
	end if;
end if;

return ds_motivo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_motivo_cancel_agenda (nr_seq_agenda_p bigint, cd_motivo_cancel_p text) FROM PUBLIC;

