CREATE OR REPLACE FUNCTION obter_motivo_cancel_ageserv (nr_sequencia_p text) RETURNS varchar AS $body$
DECLARE


ds_motivo_dom_w	varchar(255);
cd_motivo_cancel_w	agenda_consulta.cd_motivo_cancelamento%type;


BEGIN


select 	max(cd_motivo_cancelamento)
into STRICT	cd_motivo_cancel_w
from	agenda_consulta
where	nr_sequencia = nr_sequencia_p;

if (cd_motivo_cancel_w IS NOT NULL AND cd_motivo_cancel_w::text <> '') then

	select 	max(ds_valor_dominio)
	into STRICT	ds_motivo_dom_w
	from 	valor_dominio
	where  	cd_dominio  = 1011
	and	vl_dominio = cd_motivo_cancel_w;

end if;

return	ds_motivo_dom_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_motivo_cancel_ageserv (nr_sequencia_p text) FROM PUBLIC;

