CREATE OR REPLACE FUNCTION obter_motivo_cancel_checkup ( nr_seq_motivo_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(100);


BEGIN

select	substr(ds_motivo,1,100)
into STRICT	ds_retorno_w
from	checkup_motivo_cancel
where	cd_estabelecimento 	= cd_estabelecimento_p
and	nr_sequencia		= nr_seq_motivo_p;

return	ds_retorno_w;

end;
