SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_motivo_canc_agecir ( cd_motivo_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_motivo_w	varchar(255);
ds_retorno_w	varchar(255);


BEGIN
IF (cd_motivo_p IS NOT NULL AND cd_motivo_p::text <> '') THEN
	SELECT	MAX(ds_motivo)
	INTO STRICT	ds_motivo_w
	FROM	agenda_motivo_cancelamento
	WHERE	cd_motivo	=	cd_motivo_p
	AND	ie_agenda IN ('CI','T');
END IF;

IF (ie_opcao_p = 'D') THEN
	ds_retorno_w	:= ds_motivo_w;
END IF;

RETURN	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
