CREATE OR REPLACE FUNCTION obter_motivo_canc_consulta_ag ( cd_tipo_agenda_p bigint, cd_motivo_cancelamento_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN
if (cd_tipo_agenda_p = 5) then
	ds_retorno_w :=	obter_valor_dominio(1011,cd_motivo_cancelamento_p);
elsif (cd_tipo_agenda_p in (3,4)) then
	select	max(ds_motivo)
	into STRICT	ds_retorno_w
	from	agenda_motivo_cancelamento
	where	cd_motivo = cd_motivo_cancelamento_p
	and	ie_agenda in ('C','T');
elsif (cd_tipo_agenda_p = 2) then
	select	max(ds_motivo)
	into STRICT	ds_retorno_w
	from	agenda_motivo_cancelamento
	where	cd_motivo = cd_motivo_cancelamento_p
	and	ie_agenda in ('E','T');
elsif (cd_tipo_agenda_p = 1) then
	select	max(ds_motivo)
	into STRICT	ds_retorno_w
	from	agenda_motivo_cancelamento
	where	cd_motivo = cd_motivo_cancelamento_p
	and	ie_agenda in ('CI','T');
else
	ds_retorno_w := '';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
