CREATE OR REPLACE FUNCTION obter_motivo_consulta (nr_seq_agenda_p bigint) RETURNS varchar AS $body$
DECLARE


ds_motivo_w			varchar(255);
ie_status_agenda_w		varchar(3);
nr_seq_motivo_bloq_w		bigint;
cd_motivo_cancel_w		agenda_consulta.cd_motivo_cancelamento%type;
nr_seq_motivo_transf_w	bigint;


BEGIN
if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then
	/* obter dados agenda */

	select	ie_status_agenda,
		--nr_seq_motivo_bloq,
		cd_motivo_cancelamento,
		nr_seq_motivo_transf
	into STRICT	ie_status_agenda_w,
		--nr_seq_motivo_bloq_w,
		cd_motivo_cancel_w,
		nr_seq_motivo_transf_w
	from	agenda_consulta
	where	nr_sequencia = nr_seq_agenda_p;

	/* obter motivo */

	if (ie_status_agenda_w = 'B') then
		/* obter motivo bloqueio */

		/*
		select	obter_motivo_bloq_agenda(nr_seq_agenda_p, nr_seq_motivo_bloq_w)
		into	ds_motivo_w
		from	dual;
		*/
		ds_motivo_w := null;
	elsif (ie_status_agenda_w = 'C') then
		/* obter motivo cancelamento */

		/*
		select	obter_motivo_cancel_agenda(nr_seq_agenda_p, cd_motivo_cancel_w)
		into	ds_motivo_w
		from	dual;
		*/
		ds_motivo_w := null;
	elsif (nr_seq_motivo_transf_w IS NOT NULL AND nr_seq_motivo_transf_w::text <> '') then
		/* obter motivo transferÃªncia */

		select	obter_motivo_transf_consulta(nr_seq_agenda_p, nr_seq_motivo_transf_w)
		into STRICT	ds_motivo_w
		;
	end if;
end if;

return ds_motivo_w;

end;
