SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_motivo_desvio ( ie_tipo_p text) RETURNS bigint AS $body$
DECLARE


nr_sequencia_w bigint;



BEGIN
nr_sequencia_w := null;

if (ie_tipo_p <> '') then

	select 	max(nr_sequencia)
	into STRICT	nr_sequencia_w
	from 	adep_motivo_interrupcao
	where 	ie_situacao = 'A'
	and 	ie_default = 'S'
	and 	((coalesce(ie_evento::text, '') = '') or (ie_evento = ie_tipo_p));

end if;

return nr_sequencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
