CREATE OR REPLACE FUNCTION obter_motivo_fim_programa (nr_seq_motivo_fim_p bigint) RETURNS varchar AS $body$
DECLARE


ds_motivo_fim_programa_w   hfp_motivo_fim.ds_motivo%type := null;

BEGIN

if (nr_seq_motivo_fim_p IS NOT NULL AND nr_seq_motivo_fim_p::text <> '') then

select  	max(ds_motivo)
into STRICT 	ds_motivo_fim_programa_w
from 	hfp_motivo_fim
where 	nr_sequencia = nr_seq_motivo_fim_p;

end if;

return	ds_motivo_fim_programa_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
