CREATE OR REPLACE FUNCTION obter_motivo_glosa_autor_pls (nr_seq_guia_plano_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w		varchar(255);


BEGIN

if (coalesce(nr_seq_guia_plano_p,0) <> 0) then
	begin
	select	CASE WHEN count(*)=0 THEN ''  ELSE substr(max(nr_codigo) || ' - ' || max(ds_descricao_glosa_ocor),1,250) END
	into STRICT	ds_retorno_w
	from	pls_guia_glosa_ocorrencia_v
	where	nr_seq_guia = nr_seq_guia_plano_p;
	exception
	when others then
		ds_retorno_w := null;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
