CREATE OR REPLACE FUNCTION obter_motivo_glosa_ret_item (nr_interno_conta_p bigint) RETURNS varchar AS $body$
DECLARE


nr_retorno_w		integer;
cd_motivo_glosa_w		integer;


BEGIN

select	max(cd_motivo_glosa)
into STRICT	cd_motivo_glosa_w
from 	convenio_retorno_item
where	nr_interno_conta = nr_interno_conta_p;

nr_retorno_w	:= coalesce(cd_motivo_glosa_w,0);

return	nr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
