CREATE OR REPLACE FUNCTION obter_motivo_interdicao_cross (cd_motivo_p bigint, nr_seq_interno_p bigint default null) RETURNS bigint AS $body$
DECLARE


nr_retorno_w smallint;


BEGIN

SELECT	coalesce(max(cd_motivo_cross),0)
INTO STRICT	nr_retorno_w
FROM	motivo_interdicao_leito
WHERE 	cd_motivo = cd_motivo_p;

if (coalesce(nr_retorno_w, 0) = 0) then
	select max(CD_MOTIVO_INTERDICAO)
	into STRICT   nr_retorno_w
	from   unidade_atend_hist a
	where  a.nr_seq_unidade = nr_seq_interno_p
	and    a.nr_sequencia = (SELECT max(nr_sequencia) from unidade_atend_hist b where a.NR_SEQ_UNIDADE = b.NR_SEQ_UNIDADE and coalesce(b.cd_motivo_interdicao, 0) <> 0);
end if;

RETURN	nr_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
