SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_motivo_nao_lote (ds_objeto_p text) RETURNS varchar AS $body$
DECLARE


ds_motivo_w	varchar(2000);


BEGIN

if (position('EXECUTAR_MATERIAL_LIB_PRESCR' in ds_objeto_p) > 0) then
	ds_motivo_w	:= wheb_mensagem_pck.get_texto(309448); -- Shift+F11/Aplicação Principal/Faturamento/Executar mat/med ao liberar a prescrição
elsif (position('BAIXAR_MATERIAL_BARRAS_LOTE' in ds_objeto_p) > 0) then
	ds_motivo_w	:= wheb_mensagem_pck.get_texto(309449); -- Lote atendimento na função Atendimento do lote da prescrição
elsif (position('BAIXAR_PRESCRICAO_LOTE' in ds_objeto_p) > 0) then
	ds_motivo_w	:= wheb_mensagem_pck.get_texto(309450); -- Parâmetro 47 da função Atendimento do lote da Prescrição
elsif (position('BAIXAR_PRESCR_PENDENTE' in ds_objeto_p) > 0) then
	ds_motivo_w	:= wheb_mensagem_pck.get_texto(309451); -- Baixado automaticamente pela Job por não ter sido atendido
elsif (position('DEFINIR_DISPENSACAO_FARMACIA' in ds_objeto_p) > 0) then
	ds_motivo_w	:= wheb_mensagem_pck.get_texto(309452); -- Usuário definiu o item como Não necessida de dispensação, na gestão de farmácia ou enfermagem
elsif (position('GERAR_GEDI_MEDIC_ATEND' in ds_objeto_p) > 0) then
	ds_motivo_w	:= wheb_mensagem_pck.get_texto(309453); -- Medicamento possui regra de dispensação como Sistema define, não gerou porque ainda tem saldo para a medicação
elsif (position('BAIXAR_PRESCR_PEND_ACM_SN' in ds_objeto_p) > 0) then
	ds_motivo_w	:= wheb_mensagem_pck.get_texto(309454); -- Item prescrito como ACM/SN e não foi administrado/suspenso no ADEP
end if;

return	ds_motivo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
