CREATE OR REPLACE FUNCTION obter_motivo_perm_setor (nr_seq_motivo_perm_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);


BEGIN

if (nr_seq_motivo_perm_p IS NOT NULL AND nr_seq_motivo_perm_p::text <> '') and (nr_seq_motivo_perm_p > 0) then

	select	ds_motivo
	into STRICT	ds_retorno_w
	from    	motivo_transf_pac
	where   	nr_sequencia = nr_seq_motivo_perm_p;

end if;

return	ds_retorno_w;

end;
$body$
