SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_motivo_precaucao (nr_seq_precaucao_p bigint) RETURNS varchar AS $body$
DECLARE




ds_motivo_w		varchar(255);
ds_motivo_ww		varchar(255);


c01 CURSOR FOR
SELECT	substr(obter_desc_motivo_isolamento(nr_seq_motivo_isol),1,90)
from	atend_precaucao_motivo
where	nr_seq_atend_precaucao  = nr_seq_precaucao_p;


BEGIN

open C01;
loop
fetch C01 into
	ds_motivo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (coalesce(ds_motivo_ww::text, '') = '') then
		ds_motivo_ww	:=	ds_motivo_w;
	else
		ds_motivo_ww	:=	substr(ds_motivo_ww || ' / ' || ds_motivo_w,1,255);
	end if;
	end;
end loop;
close C01;


return	ds_motivo_ww;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_motivo_precaucao (nr_seq_precaucao_p bigint) FROM PUBLIC;

