CREATE OR REPLACE FUNCTION obter_motivo_solic_setor ( cd_setor_atendimento_p bigint, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_retorno_w			bigint;

c01 CURSOR FOR
SELECT	a.nr_sequencia
from	motivo_solic_compra a
where	coalesce(a.cd_estabelecimento,cd_estabelecimento_p) = cd_estabelecimento_p
and	exists (select	1
		from	motivo_solic_compra_setor x
		where	x.nr_seq_motivo_solic = a.nr_sequencia
		and	x.cd_setor_atendimento = cd_setor_atendimento_p)
order by	coalesce(a.cd_estabelecimento,0);


BEGIN

open c01;
loop
fetch c01 into
	nr_seq_retorno_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
end loop;
close c01;

return	nr_seq_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
