CREATE OR REPLACE FUNCTION obter_motivo_transf_vaga_pend ( nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE

 
cd_unidade_basica_w 	varchar(10);
cd_unidade_compl_w	varchar(10);
cd_pessoa_fisica_w	varchar(10);
cd_setor_atendimento_w	integer;
nr_seq_motivo_transf_w	bigint;
ie_pesquisa_w		varchar(1) := 'S';


BEGIN 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
	begin		 
		begin 
		select	a.cd_unidade_basica, 
			a.cd_unidade_compl, 
			a.cd_setor_atendimento 
		into STRICT	cd_unidade_basica_w, 
			cd_unidade_compl_w, 
			cd_setor_atendimento_w 
		from	unidade_atendimento a, 
			atendimento_paciente b 
		where	a.ie_status_unidade 	= 'R' 
		and	a.cd_paciente_reserva 	= b.cd_pessoa_fisica 
		and	b.nr_atendimento = nr_atendimento_p;
		Exception when others then 
			ie_pesquisa_w	:= 'N';
		end;
	if (ie_pesquisa_w = 'S') then 
		begin 
		select	max(nr_seq_motivo_transf) 
		into STRICT	nr_seq_motivo_transf_w 
		from	gestao_vaga 
		where	ie_status = 'R' 
		and	nr_atendimento 		= nr_atendimento_p 
		and	cd_setor_desejado 	= cd_setor_atendimento_w 
		and	cd_unidade_basica 	= cd_unidade_basica_w 
		and	cd_unidade_compl 	= cd_unidade_compl_w;
		end;
	end if;	
	end;
end if;
return nr_seq_motivo_transf_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
