CREATE OR REPLACE FUNCTION obter_motivo_urg_solic (nr_solic_compra_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_motivo_w		varchar(80);


BEGIN 
 
select	max(b.ds_motivo) 
into STRICT	ds_motivo_w 
from	solic_compra a, 
	motivo_urgencia_compras b 
where	a.nr_solic_compra = nr_solic_compra_p 
and	a.nr_seq_motivo_urgente = b.nr_sequencia;
 
return	ds_motivo_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
