CREATE OR REPLACE FUNCTION obter_mot_canc_quimio (nr_seq_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_mot_cancelamento_w	bigint;
ds_retorno_w		varchar(255);


BEGIN

select	max(nr_seq_mot_cancelamento)
into STRICT	nr_seq_mot_cancelamento_w
from	agenda_quimio
where	nr_seq_atendimento = nr_seq_atendimento_p;

select	max(ds_motivo)
into STRICT	ds_retorno_w
from	qt_motivo_cancelamento
where	nr_sequencia = nr_seq_mot_cancelamento_w;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
