SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_mot_lib_acomp_aten (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_motivo_liberacao_w	varchar(255);
dt_liberacao_w		timestamp;
nr_sequencia_w		bigint;
nm_usuario_mot_lib_w	varchar(15);
nr_seq_motivo_w		bigint;
ds_retorno_w		varchar(280);


BEGIN

ds_retorno_w := '';

if (coalesce(nr_atendimento_p,0) > 0) then

	select	max(dt_liberacao)
	into STRICT	dt_liberacao_w
	from	atendimento_historico
	where	nr_atendimento = nr_atendimento_p
	and	(nr_seq_motivo_lib IS NOT NULL AND nr_seq_motivo_lib::text <> '')
	and	(nm_usuario_motivo_lib IS NOT NULL AND nm_usuario_motivo_lib::text <> '');

	if (dt_liberacao_w IS NOT NULL AND dt_liberacao_w::text <> '') then


		select	coalesce(max(nr_sequencia),0)
		into STRICT	nr_sequencia_w
		from	atendimento_historico
		where	nr_atendimento = nr_atendimento_p
		and	dt_liberacao   = dt_liberacao_w;

		if (nr_sequencia_w > 0) then

			select	max(nr_seq_motivo_lib),
				substr(max(nm_usuario_motivo_lib),1,15)
			into STRICT	nr_seq_motivo_w,
				nm_usuario_mot_lib_w
			from	atendimento_historico
			where	nr_sequencia = nr_sequencia_w
			and	nr_atendimento = nr_atendimento_p;

			select	substr(ds_motivo_liberacao,1,255)
			into STRICT	ds_motivo_liberacao_w
			from	motivo_liberacao_acomp
			where	nr_sequencia = nr_seq_motivo_w;

			ds_retorno_w := substr(ds_motivo_liberacao_w || ' / ' || nm_usuario_mot_lib_w,1,280);

		end if;

	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_mot_lib_acomp_aten (nr_atendimento_p bigint) FROM PUBLIC;

