SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_mov_musc_ashworth (nr_seq_item_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000);
nr_seq_mov_w		bigint;
nr_seq_musc_w		bigint;
IE_MUSCULATURA_w	varchar(10);
ds_musculatura_w	varchar(255);

BEGIN

select	max(nr_seq_movimento),
		max(nr_seq_musculo),
		max(IE_MUSCULATURA)
into STRICT	nr_seq_mov_w,
		nr_seq_musc_w,
		IE_MUSCULATURA_w
from	artic_mov_musculo
where	nr_sequencia = nr_seq_item_p;


if (IE_MUSCULATURA_w IS NOT NULL AND IE_MUSCULATURA_w::text <> '') then

	if (IE_MUSCULATURA_w	= 'P') then
		ds_musculatura_w	:= wheb_mensagem_pck.get_texto(309412); -- Principal
	else
		ds_musculatura_w	:= wheb_mensagem_pck.get_texto(309414); -- Acess√≥rio
	end if;

end if;

if (nr_seq_mov_w IS NOT NULL AND nr_seq_mov_w::text <> '') then
	ds_retorno_w := obter_desc_movimento(nr_seq_mov_w)||' - ';
end if;

ds_retorno_w	:=	ds_retorno_w||obter_desc_musculo(nr_seq_musc_w);

if (ds_musculatura_w IS NOT NULL AND ds_musculatura_w::text <> '') then
	ds_retorno_w	:= ds_retorno_w||' ( '||ds_musculatura_w||' )';
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
