SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_msg_alerta_api ( nr_prescricao_p bigint, nr_seq_material_p bigint, cd_api_p text) RETURNS varchar AS $body$
DECLARE


	ds_retorno_w	varchar(4000);
	ds_mensagem_w	varchar(4000);

	c01 CURSOR FOR
	SELECT 	coalesce(ds_resultado_curto,'')
	from 	alerta_api 
	where 	nr_prescricao = nr_prescricao_p 
	and 	nr_seq_material 	= nr_seq_material_p
	and 	cd_api 	= cd_api_p
	and     coalesce(ie_status_requisicao::text, '') = '';


BEGIN
	ds_retorno_w := '';
	if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '') then
		open c01;
		loop
		fetch c01 into ds_mensagem_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin
				if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
					ds_retorno_w := ds_retorno_w || chr(10) || chr(13) ||ds_mensagem_w;
				else
					ds_retorno_w := ds_mensagem_w;
				end if;
			end;
		end loop;
		close c01;
	end if;

	return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
