SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_msg_class_leite_deriv ( nr_prescricao_p bigint, nr_seq_material_p bigint, nm_usuario_p text, qt_classif_igual_p bigint, qt_classif_dif_p bigint) RETURNS varchar AS $body$
DECLARE


dt_inicio_item_w	timestamp;
dt_inicio_prescr_w	timestamp;
cd_material_w		bigint;
ds_classif_w		varchar(2000);
ds_material_w		varchar(2000);
ds_retorno_w		varchar(4000);
nr_atendimento_w	bigint;
qt_classif_w		bigint;

C01 CURSOR FOR
	SELECT	substr(obter_desc_material(b.cd_material),1,100)
	from	prescr_material b,
		prescr_medica a                    
	where	a.nr_atendimento = nr_atendimento_w
	and	a.nr_prescricao = b.nr_prescricao
	and	coalesce(a.dt_suspensao::text, '') = ''
	and	a.dt_prescricao between clock_timestamp() - interval '5 days' and clock_timestamp() + interval '1 days'
	and	((a.nm_usuario_original = nm_usuario_p) or ((coalesce(a.dt_liberacao,a.dt_liberacao_medico) IS NOT NULL AND (coalesce(a.dt_liberacao,a.dt_liberacao_medico))::text <> '')))
	and	dt_inicio_item_w between obter_data_leite_deriv(a.nr_prescricao,'m') and obter_data_leite_deriv(a.nr_prescricao,'M')
	and	b.ie_agrupador = 16
	and	coalesce(b.dt_suspensao::text, '') = ''
	and	coalesce(b.ie_suspenso,'N') = 'N'
	and	(obter_classif_mat_leite_deriv(b.cd_material) IS NOT NULL AND (obter_classif_mat_leite_deriv(b.cd_material))::text <> '')   
	and	obter_classif_mat_leite_deriv(b.cd_material) = obter_classif_mat_leite_deriv(cd_material_w)
	order by 1;

BEGIN

select	ESTABLISHMENT_TIMEZONE_UTILS.dateAtTime(a.dt_inicio_prescr, coalesce(coalesce(b.hr_prim_horario,b.hr_dose_especial), '00:00')),
	b.cd_material,
	Obter_classif_leite_deriv(obter_classif_mat_leite_deriv(b.cd_material)),
	nr_atendimento
into STRICT	dt_inicio_item_w,
	cd_material_w,
	ds_classif_w,
	nr_atendimento_w
from	prescr_medica a,
	prescr_material b
where	a.nr_prescricao = nr_prescricao_p
and	a.nr_prescricao = b.nr_prescricao
and	b.nr_sequencia = nr_seq_material_p;

if (qt_classif_igual_p > 1) then

	ds_retorno_w := wheb_mensagem_pck.get_texto(309415) || ': ' || ds_classif_w; -- Classificacao duplicada
	open C01;
	loop
	fetch C01 into	
		ds_material_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		ds_retorno_w := substr(ds_retorno_w || chr(13) || ' - ' || ds_material_w,1,4000);
		end;
	end loop;
	close C01;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_msg_class_leite_deriv ( nr_prescricao_p bigint, nr_seq_material_p bigint, nm_usuario_p text, qt_classif_igual_p bigint, qt_classif_dif_p bigint) FROM PUBLIC;

