SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_msg_consistir_regra_rep (nr_seq_regra_p bigint, cd_perfil_p bigint) RETURNS varchar AS $body$
DECLARE


ds_mensagem_consistir_w	varchar(4000);
ie_msg_perfil_w		varchar(1)	:= 'N';

c01 CURSOR FOR
SELECT	ds_mensagem_cliente
from	regra_consiste_prescr_par
where	nr_seq_regra 		= nr_seq_regra_p
and	coalesce(cd_perfil,cd_perfil_p)	= cd_perfil_p
order by
	nr_sequencia;


BEGIN
if (nr_seq_regra_p IS NOT NULL AND nr_seq_regra_p::text <> '') then
	open c01;
	loop
	fetch c01 into ds_mensagem_consistir_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		ie_msg_perfil_w		:= 'S';
		ds_mensagem_consistir_w	:= ds_mensagem_consistir_w;
		end;
	end loop;
	close c01;

	if (ie_msg_perfil_w = 'N') then
		select	obter_desc_expressao(cd_exp_mensagem,ds_mensagem_usuario)
		into STRICT	ds_mensagem_consistir_w
		from	regra_consiste_prescr
		where	nr_sequencia = nr_seq_regra_p;
	end if;
else
	ds_mensagem_consistir_w	:= '';
end if;

return ds_mensagem_consistir_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
