CREATE OR REPLACE FUNCTION obter_msg_cons_preco ( cd_material_p bigint, dt_consulta_p timestamp, ie_tipo_atendimento_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);
cd_grupo_material_w		smallint	:= 0;
cd_subgrupo_material_w		smallint	:= 0;
cd_classe_material_w		integer	:= 0;
nr_seq_familia_w		bigint;
ie_padronizado_w		varchar(1);
ie_cobra_paciente_w		varchar(1);
ds_mensagem_w			varchar(255);
vl_material_w			double precision;
cd_convenio_w			integer;
cd_categoria_w			varchar(10);
dt_consulta_w			timestamp;

C01 CURSOR FOR
	SELECT	cd_convenio,
		cd_categoria,
		vl_material
	from	w_consulta_preco
	where	nm_usuario = nm_usuario_p
	and 	cd_material = cd_material_p
	and 	ie_tipo_item = 2;

C02 CURSOR FOR
	SELECT	ds_mensagem
	from	regra_msg_cons_preco
	where	coalesce(cd_convenio, coalesce(cd_convenio_w,0)) = coalesce(cd_convenio_w,0)
	and 	coalesce(cd_categoria, coalesce(cd_categoria_w,0)) = coalesce(cd_categoria_w,0)
	and 	coalesce(ie_tipo_atendimento, coalesce(ie_tipo_atendimento_p,0)) = coalesce(ie_tipo_atendimento_p,0)
	and 	trunc(dt_consulta_w,'dd') between trunc(dt_vigencia_inicial,'dd') and coalesce(dt_vigencia_final, dt_consulta_w)
	and 	coalesce(cd_material, cd_material_p) = cd_material_p
	and 	coalesce(cd_grupo_material, cd_grupo_material_w) = cd_grupo_material_w
	and 	coalesce(cd_subgrupo_material, cd_subgrupo_material_w) = cd_subgrupo_material_w
	and 	coalesce(cd_classe_material, cd_classe_material_w) = cd_classe_material_w
	and 	coalesce(nr_seq_familia, nr_seq_familia_w) = nr_seq_familia_w
	and 	((coalesce(ie_padronizado,'A') = 'A') or (ie_padronizado = ie_padronizado_w))
	and 	((coalesce(ie_cobra_paciente,'A') = 'A') or (ie_cobra_paciente = ie_cobra_paciente_w))
	and 	((coalesce(ie_preco,'A') = 'A') or (ie_preco = 'N' AND vl_material_w = 0) or (ie_preco = 'S' AND vl_material_w > 0))
	and	((coalesce(nr_seq_estrutura::text, '') = '') or (consistir_se_mat_estrut_vig(nr_seq_estrutura,cd_material_p, dt_consulta_p) = 'S'))
	order by
		coalesce(cd_material,0),
		coalesce(nr_seq_estrutura,0),
		coalesce(nr_seq_familia,0),
		coalesce(cd_classe_material,0),
		coalesce(cd_subgrupo_material,0),
		coalesce(cd_grupo_material,0),
		coalesce(ie_tipo_atendimento,0),
		coalesce(cd_categoria,'0'),
		coalesce(cd_convenio,0),
		dt_vigencia_inicial;



BEGIN

ds_retorno_w	:= '';
ds_mensagem_w	:= '';
dt_consulta_w	:= dt_consulta_p;

select	coalesce(max(a.cd_classe_material),0),
	coalesce(max(a.cd_subgrupo_material),0),
	coalesce(max(a.cd_grupo_material),0),
	coalesce(max(a.nr_seq_familia),0)
into STRICT	cd_classe_material_w,
	cd_subgrupo_material_w,
	cd_grupo_material_w,
	nr_seq_familia_w
from	estrutura_material_v a
where	cd_material = cd_material_p;

select 	max(ie_padronizado),
	max(ie_cobra_paciente)
into STRICT	ie_padronizado_w,
	ie_cobra_paciente_w
from 	material
where 	cd_material = cd_material_p;

open C01;
loop
fetch C01 into
	cd_convenio_w,
	cd_categoria_w,
	vl_material_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin


	ds_mensagem_w:= '';

	open C02;
	loop
	fetch C02 into
		ds_mensagem_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

		ds_retorno_w:= ds_mensagem_w;

		end;
	end loop;
	close C02;

	end;
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
