CREATE OR REPLACE FUNCTION obter_msg_control_medic (nr_dias_p bigint, cd_material_p bigint, cd_pessoa_fisica_p text, ie_objetivo_p text) RETURNS varchar AS $body$
DECLARE


ds_mensagem_w				varchar(255);
cd_grupo_material_w			smallint;
cd_subgrupo_material_w			smallint;
cd_classe_material_w			integer;


BEGIN

select	max(cd_grupo_material),
	max(cd_subgrupo_material),
	max(cd_classe_material)
into STRICT	cd_grupo_material_w,
	cd_subgrupo_material_w,
	cd_classe_material_w
from	estrutura_material_v
where	cd_material = cd_material_p;

select	max(ds_mensagem)
into STRICT 	ds_mensagem_w
from	rep_regra_troca_medic
where	coalesce(cd_material,cd_material_p) 	= cd_material_p
and	coalesce(cd_grupo_material, cd_grupo_material_w)		= cd_grupo_material_w
and	coalesce(cd_subgrupo_material, cd_subgrupo_material_w)	= cd_subgrupo_material_w
and	coalesce(cd_classe_material, cd_classe_material_w)		= cd_classe_material_w
and	((nr_dias	= nr_dias_p) or (coalesce(nr_dias,0) = 0))
and	((coalesce(cd_perfil::text, '') = '') or (cd_perfil = obter_Perfil_ativo))
and	((ie_objetivo	= ie_objetivo_p) or (coalesce(ie_objetivo::text, '') = ''))
and	coalesce(ie_situacao,'A') = 'A'
and	((cd_pessoa_fisica = cd_pessoa_fisica_p) or (coalesce(cd_pessoa_fisica::text, '') = ''));

return	ds_mensagem_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
