	cod_msg_w := 253343; -- Deve-se informar o nome do médico externo, parâmetro[1056]. 
elsif (ie_obriga_medico_externo_p = 'C') and (coalesce(crm_medico_externo_p::text, '') = '') then
	 
	cod_msg_w := 253348; -- Deve-se informar o CRM do médico externo, parâmetro[1056]. 
else
	cod_msg_w := 0;
end if;
 
return	cod_msg_w;
 
end;
