SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_msg_regra_prot_onc ( nr_seq_paciente_p bigint, nr_seq_regra_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE




ds_retorno_w		varchar(255);
qt_itens_w			integer := 0;
nm_usuario_w		varchar(15);

BEGIN

ds_retorno_w	:= '';
nm_usuario_w 	:= wheb_usuario_pck.get_nm_usuario;

if (nr_seq_paciente_p IS NOT NULL AND nr_seq_paciente_p::text <> '') and (nr_seq_regra_p IS NOT NULL AND nr_seq_regra_p::text <> '') then


	if (coalesce(ie_opcao_p,'P') = 'P') THEN

		select  count(*)
		into STRICT	qt_itens_w
		from	paciente_atendimento_erro
		where	nr_seq_paciente = nr_seq_paciente_p
		and		nm_usuario = nm_usuario_w
		and		nr_seq_regra = nr_seq_regra_p;

	else

		select  count(*)
		into STRICT	qt_itens_w
		from	paciente_atendimento_erro
		where	nr_seq_atendimento = nr_seq_paciente_p
		and		nm_usuario = nm_usuario_w
		and		nr_seq_regra = nr_seq_regra_p;


	end if;

	if (qt_itens_w > 0) then

		if (nr_seq_regra_p = 10) then
			ds_retorno_w	:= substr(obter_texto_tasy(274235, wheb_usuario_pck.get_nr_seq_idioma),1,255);
		end if;

	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
