SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_msg_sol_prot_rotina ( nr_seq_solucao_p bigint, cd_protocolo_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w					varchar(4000);

c01 CURSOR FOR
SELECT	b.ds_mensagem ds_mensagem,
       	b.ds_material ds_material
from	protocolo_medic_material a,
		material b
where  	nr_seq_solucao = nr_seq_solucao_p
and 	cd_protocolo   = cd_protocolo_p
and   	nr_sequencia   = nr_sequencia_p
and	    a.cd_material  = b.cd_material;

BEGIN

ds_retorno_w := '';

if (coalesce(nr_seq_solucao_p,0) > 0) then
	for c01_w in C01
	loop
		if (c01_w.ds_mensagem IS NOT NULL AND c01_w.ds_mensagem::text <> '') then
			if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
                			ds_retorno_w := substr(ds_retorno_w || ';---' || wheb_mensagem_pck.get_texto(1076945) --'Mensagem para o'
                			|| ' ' || C01_w.ds_material || ': ' || c01_w.ds_mensagem,0,4000);
           		 	else
            	    			ds_retorno_w := substr(ds_retorno_w || wheb_mensagem_pck.get_texto(1076945) --'Mensagem para o'
               				|| ' ' || C01_w.ds_material || ': ' || c01_w.ds_mensagem,0,4000);
            			end if;
		end if;	
	end loop;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
