CREATE OR REPLACE FUNCTION obter_msg_variacao_peso ( cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_mensagem_w	varchar(255);
ie_sexo_w		varchar(5);
ds_peso_ant_w	varchar(255);
ds_peso_atual_w	varchar(255);
qt_idade_w		double precision;
pr_variacao_w	double precision;
qt_variacao_w	double precision;
qt_peso_ant_w	double precision;
qt_peso_atual_w	double precision;
nr_seq_ult_peso_w	bigint;
qt_w			bigint;


BEGIN

select	count(*)
into STRICT	qt_w
from	Regra_variacao_peso_pac;

if (qt_w > 0) then

	select	coalesce(max(nr_sequencia),0),
			max(qt_peso)
	into STRICT	nr_seq_ult_peso_w,
			qt_peso_atual_w
	from	atendimento_sinal_vital
	where	nr_sequencia	 = (	SELECT	coalesce(max(nr_sequencia),0)
									from	atendimento_sinal_vital
									where	(qt_peso IS NOT NULL AND qt_peso::text <> '')
									and		DT_SINAL_VITAL between clock_timestamp() - interval '30 days' and clock_timestamp()
									and		cd_paciente = cd_pessoa_fisica_p
									and		coalesce(ie_situacao,'A') = 'A'
									and		(dt_liberacao IS NOT NULL AND dt_liberacao::text <> ''));

	select	max(qt_peso)
	into STRICT	qt_peso_ant_w
	from	atendimento_sinal_vital
	where	cd_paciente = cd_pessoa_fisica_p
	and		DT_SINAL_VITAL between clock_timestamp() - interval '30 days' and clock_timestamp()
	and		(qt_peso IS NOT NULL AND qt_peso::text <> '')
	and		coalesce(ie_situacao,'A') = 'A'
	and		nr_sequencia <> nr_seq_ult_peso_w
	and		(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');

	if (qt_peso_ant_w	> 0) and (qt_peso_atual_w > 0) then
		qt_variacao_w	:= qt_peso_atual_w - qt_peso_ant_w;
		pr_variacao_w	:= abs(dividir((qt_variacao_w * 100), qt_peso_ant_w));

		select	max(obter_idade(dt_nascimento,coalesce(dt_obito,clock_timestamp()),'DIA')),
				max(ie_sexo)
		into STRICT	qt_idade_w,
				ie_sexo_w
		from	pessoa_fisica
		where	cd_pessoa_fisica = cd_pessoa_fisica_p;

		select	max(ds_mensagem)
		into STRICT	ds_mensagem_w
		from	Regra_variacao_peso_pac
		where (qt_idade_w between	obter_idade_conversao(qt_idade_min,qt_idade_min_mes,qt_idade_min_dia,qt_idade_max,qt_idade_max_mes,qt_idade_max_dia,'MIN') and
					obter_idade_conversao(qt_idade_min,qt_idade_min_mes,qt_idade_min_dia,qt_idade_max,qt_idade_max_mes,qt_idade_max_dia,'MAX'))
		and		((coalesce(ie_sexo::text, '') = '') or (ie_sexo = ie_sexo_w))
		and		pr_variacao_w between coalesce(PR_VARIACAO_PESO,0) and coalesce(PR_VARIACAO_PESO_ATE,999);

		if (ds_mensagem_w IS NOT NULL AND ds_mensagem_w::text <> '') then
			ds_peso_atual_w	:= qt_peso_atual_w;
			if (qt_peso_atual_w < 1) and (qt_peso_atual_w > 0) then
				ds_peso_atual_w	:= '0' ||qt_peso_atual_w;
			end if;

			ds_peso_ant_w	:=  qt_peso_ant_w;
			if (qt_peso_ant_w < 1) and (qt_peso_ant_w > 0) then
				ds_peso_ant_w	:= '0' ||qt_peso_ant_w;
			end if;

			ds_mensagem_w	:= ds_mensagem_w || CHR(10) || CHR(10) || obter_desc_expressao(295705) || ': ' || ds_peso_atual_w || ' kg. '|| obter_desc_expressao(748378) ||': ' || ds_peso_ant_w||' kg.';
		end if;
	end if;
end if;

return	ds_mensagem_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_msg_variacao_peso ( cd_pessoa_fisica_p text) FROM PUBLIC;

