CREATE OR REPLACE FUNCTION obter_multi_classif_convenio (cd_convenio_p bigint) RETURNS varchar AS $body$
DECLARE


ds_classificacao_w	classificacao_convenio.ds_classificacao%type;
ds_retorno_w		varchar(1000);

c01 CURSOR FOR
	SELECT	ccv.ds_classificacao
	from	convenio_classif cc,
		classificacao_convenio ccv
	where	ccv.nr_sequencia	= cc.nr_seq_classificacao
	and	cc.cd_convenio	= cd_convenio_p;
	

BEGIN
	open C01;
		loop
			fetch C01 into
				ds_classificacao_w;
			EXIT WHEN NOT FOUND; /* apply on C01 */
				if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
					ds_retorno_w := ds_retorno_w || ', ' || ds_classificacao_w;
				else
					ds_retorno_w := ds_classificacao_w;
				end if;
		end loop;
	close C01;

return	ds_retorno_w;
	
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
