SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_municipio_convenio (nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


cd_municipio_w		integer;
ds_municipio_w		varchar(100)	:= null;


BEGIN


select	coalesce(max(CD_MUNICIPIO_CONVENIO),0)
into STRICT	cd_municipio_w
from	atend_categoria_convenio
where	nr_atendimento	= nr_atendimento_p;

if (cd_municipio_w <> 0) then
	select	substr(obter_desc_cep_loc(CD_MUNICIPIO_w),1,55) ds_municipio
	into STRICT	ds_municipio_w
	;
end if;

if (ie_opcao_p = 'C') then
	return	cd_municipio_w;
else
	return	ds_municipio_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
