CREATE OR REPLACE FUNCTION obter_muster_json ( nr_sequencia_p bigint, ds_tipo_xml_p text ) RETURNS text AS $body$
DECLARE

    ds_xml_type_issue_c CONSTANT varchar(1) := 'E';
    ds_xml_return_w     text;

BEGIN
    IF (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '' AND ds_tipo_xml_p IS NOT NULL AND ds_tipo_xml_p::text <> '') THEN
    BEGIN
        IF ( ds_tipo_xml_p = ds_xml_type_issue_c ) THEN
            ds_xml_return_w := telematic_json_pck.get_muster_information_clob(nr_sequencia_p);
        ELSE
            ds_xml_return_w := telematic_json_pck.get_muster_cancelation_clob(nr_sequencia_p);
        END IF;
    END;
    END IF;

    RETURN ds_xml_return_w;
END;
$body$
