SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_nas_atend ( nr_atendimento_p bigint, dt_data_p text, ds_campo_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(100);
nr_sequencia_w		bigint;
dt_data_w		varchar(10);



BEGIN

if (length(dt_data_p) < 10) then
	dt_data_w := substr('0' || dt_data_p,1,10);
else
	dt_data_w := substr(dt_data_p,1,10);
end if;


select	max(nr_sequencia)
into STRICT	nr_sequencia_w
from	escala_nas a
where	a.dt_avaliacao = ( 	SELECT	max(e.dt_avaliacao)
				from	escala_nas e
				where	e.nr_atendimento = nr_atendimento_p
				and	to_char(e.dt_avaliacao,'dd/mm/yyyy') = dt_data_w)
and	a.nr_atendimento = nr_atendimento_p;


select	CASE WHEN obter_select_concatenado_bv('select '|| ds_campo_p || ' from escala_nas where nr_sequencia = :nr_sequencia','nr_sequencia='|| nr_sequencia_w,'')='S' THEN 'X'  ELSE '' END
into STRICT	ds_retorno_w
;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
