SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_naturalidade_pf ( cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ X ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
	ie_opçao_p
		M - Município
		UF - Unidade da federação
		A - Ambos

dhoffman - 02/01/2015 - Retirado o "elsif" pois se o CEP da cidade de nascimnto não estiver informado na "cep_loc"
deve ser verificado também se foi informado um município IBGE de nascimento
-------------------------------------------------------------------------------------------------------------------
Referências:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_municipio_w			varchar(80);
sg_estado_w			cep_loc.ds_uf%type;
nr_cep_cidade_nasc_w		varchar(15);
ds_retorno_w			varchar(255);
cd_municipio_ibge_w		varchar(6);
ds_municipio_ibge_w		varchar(100);
ds_uf_ibge_w			varchar(2);


BEGIN
/*
Rafael 21/1/8 tratar município IBGE x Cidade Natal
*/
select	max(nr_cep_cidade_nasc),
	max(cd_municipio_ibge)
into STRICT	nr_cep_cidade_nasc_w,
	cd_municipio_ibge_w
from	pessoa_fisica
where	cd_pessoa_fisica	= cd_pessoa_fisica_p;

if (nr_cep_cidade_nasc_w IS NOT NULL AND nr_cep_cidade_nasc_w::text <> '') then
	begin
	select	coalesce(max(nm_localidade),''),
		coalesce(max(ds_uf),'')
	into STRICT	ds_municipio_w,
		sg_estado_w
	from	cep_loc
	where	cd_cep	= nr_cep_cidade_nasc_w;
	end;

	if (ie_opcao_p = 'M') then
		ds_retorno_w	:= ds_municipio_w;
	elsif (ie_opcao_p = 'UF') then
		ds_retorno_w	:= sg_estado_w;
	else
		begin
		if (ds_municipio_w = '') then
			select	coalesce(max(nm_localidade),''),
				coalesce(max(cd_unidade_federacao),'')
			into STRICT	ds_municipio_w,
				sg_estado_w
			from	cep_localidade
			where	cd_localidade	= nr_cep_cidade_nasc_w;
		end if;

		ds_retorno_w	:= ds_municipio_w || ' ' || sg_estado_w;
		end;
	end if;
end if;

if (cd_municipio_ibge_w IS NOT NULL AND cd_municipio_ibge_w::text <> '') and (coalesce(trim(both ds_retorno_w)::text, '') = '') then
	select	max(ds_municipio),
		max(ds_unidade_federacao)
	into STRICT	ds_municipio_ibge_w,
		ds_uf_ibge_w
	from	sus_municipio
	where	cd_municipio_ibge	= cd_municipio_ibge_w;

	if (ie_opcao_p = 'M') then
		ds_retorno_w	:= ds_municipio_ibge_w;
	elsif (ie_opcao_p = 'UF') then
		ds_retorno_w	:= ds_uf_ibge_w;
	else
		ds_retorno_w	:= ds_municipio_ibge_w || ' ' || ds_uf_ibge_w;
	end if;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_naturalidade_pf ( cd_pessoa_fisica_p text, ie_opcao_p text) FROM PUBLIC;

