SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_nfe_conta_protocolo (nr_seq_protocolo_p bigint, nr_interno_conta_p bigint) RETURNS varchar AS $body$
DECLARE
	

nr_nota_w		varchar(255);
ds_nota_w		varchar(4000) := ' ';

c01 CURSOR FOR
SELECT	a.nr_nfe_imp
from	nota_fiscal a
where (nr_seq_protocolo_p 	<> 0)
and (a.nr_seq_protocolo 	= nr_seq_protocolo_p)
and	a.ie_situacao = '1'
and (nr_interno_conta_p	= a.nr_interno_conta or
	nr_interno_conta_p	= 0)

union

SELECT	a.nr_nfe_imp
from	nota_fiscal a
where	nr_interno_conta_p	= a.nr_interno_conta
and	a.ie_situacao = '1'
and	nr_interno_conta_p	<> 0

union

select	a.nr_nfe_imp
from	nota_fiscal a,
	protocolo_convenio b
where	a.nr_seq_lote_prot	= b.nr_seq_lote_protocolo
and	nr_seq_protocolo_p	<> 0
and	a.ie_situacao = '1'
and	b.nr_seq_protocolo	= nr_seq_protocolo_p
order by 1;


BEGIN
open c01;
loop
fetch c01 into nr_nota_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	if	((length(ds_nota_w) < 3600) and (nr_nota_w IS NOT NULL AND nr_nota_w::text <> '')) then
		ds_nota_w := ds_nota_w || nr_nota_w ||', ';
	end if;
end loop;
close c01;

return substr(ds_nota_w,1,length(ds_nota_w) - 2);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
