CREATE OR REPLACE FUNCTION obter_nfe_titulo_receb ( nr_seq_nf_saida_p text, nr_interno_conta_p bigint, nr_seq_protocolo_p bigint) RETURNS varchar AS $body$
DECLARE


nr_nfe_imp_w		nota_fiscal.nr_nfe_imp%type;


BEGIN

if (nr_seq_nf_saida_p IS NOT NULL AND nr_seq_nf_saida_p::text <> '') then
	select	max(nr_nfe_imp)
	into STRICT	nr_nfe_imp_w
	from	nota_fiscal
	where	nr_sequencia = nr_seq_nf_saida_p;
elsif (nr_interno_conta_p IS NOT NULL AND nr_interno_conta_p::text <> '') then
	select	max(nr_nfe_imp)
	into STRICT	nr_nfe_imp_w
	from	nota_fiscal
	where	nr_interno_conta = nr_interno_conta_p;
elsif (nr_seq_protocolo_p IS NOT NULL AND nr_seq_protocolo_p::text <> '') then
	select	max(nr_nfe_imp)
	into STRICT	nr_nfe_imp_w
	from	nota_fiscal
	where	nr_seq_protocolo = nr_seq_protocolo_p;
end if;

return	nr_nfe_imp_w;

end;
$body$
