SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_nf_caixa_rec ( nr_seq_caixa_rec_p text) RETURNS varchar AS $body$
DECLARE

 
nr_nota_fiscal_w		varchar(255) := '';
ds_nota_fiscal_w		varchar(255):= '';
nr_interno_conta_w		bigint;

c01 CURSOR FOR 
SELECT 	a.nr_interno_conta 
FROM  	titulo_receber a, 
	titulo_receber_liq c 
WHERE 	a.nr_titulo		= c.nr_titulo 
and	c.nr_seq_caixa_rec	= nr_seq_caixa_rec_p;

c02 CURSOR FOR 
SELECT	nr_nota_fiscal 
FROM 	nota_fiscal 
WHERE 	nr_interno_conta	= nr_interno_conta_w;


BEGIN 
 
OPEN C01;
LOOP 
FETCH C01 into 
	nr_interno_conta_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
 
	OPEN C02;
	LOOP 
	FETCH C02 into 
		nr_nota_fiscal_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
 
 
		if (coalesce(ds_nota_fiscal_w::text, '') = '') then 
			ds_nota_fiscal_w := substr(nr_nota_fiscal_w,1,254);
		else 
			ds_nota_fiscal_w := substr(ds_nota_fiscal_w ||', '|| nr_nota_fiscal_w,1,254);
		end if;
 
	END LOOP;
	CLOSE C02;
 
END LOOP;
CLOSE C01;
 
return ds_nota_fiscal_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
