CREATE OR REPLACE FUNCTION obter_nf_cheque_cp (nr_seq_cheque_p bigint) RETURNS varchar AS $body$
DECLARE


nr_nota_fiscal_w	varchar(255);
ds_notas_w		varchar(255);

c01 CURSOR FOR
SELECT	c.nr_nota_fiscal || ', '
from	nota_fiscal c,
	titulo_pagar b,
	CHEQUE_BORDERO_TITULO a
WHERE	a.nr_seq_cheque		= nr_seq_cheque_p
and	a.nr_titulo		= b.nr_titulo
and	b.nr_seq_nota_fiscal	= c.nr_sequencia;


BEGIN

open c01;
loop
fetch c01 into
	nr_nota_fiscal_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	ds_notas_w := substr(ds_notas_w || nr_nota_fiscal_w,1,254);
end loop;
close c01;

return	ds_notas_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
