CREATE OR REPLACE FUNCTION obter_nf_conta_geral ( nr_interno_conta_p bigint) RETURNS bigint AS $body$
DECLARE

 
nr_seq_protocolo_w	conta_paciente.nr_seq_protocolo%type;
nr_seq_nota_fiscal_w	nota_fiscal.nr_sequencia%type;
nr_seq_conta_origem_w	conta_paciente.nr_seq_conta_origem%type;


BEGIN 
 
select	max(a.nr_seq_protocolo), 
	max(a.nr_seq_conta_origem) 
into STRICT	nr_seq_protocolo_w, 
	nr_seq_conta_origem_w 
from	conta_paciente a 
where	a.nr_interno_conta	= nr_interno_conta_p;
 
select	max(a.nr_sequencia) 
into STRICT	nr_seq_nota_fiscal_w 
from	nota_fiscal a 
where	coalesce(a.nr_interno_conta::text, '') = '' 
and	a.nr_seq_protocolo	= nr_seq_protocolo_w;
 
if (coalesce(nr_seq_nota_fiscal_w::text, '') = '') then 
 
	select	max(a.nr_sequencia) 
	into STRICT	nr_seq_nota_fiscal_w 
	from	nota_fiscal a 
	where	a.nr_interno_conta	= nr_interno_conta_p;
 
	if (coalesce(nr_seq_nota_fiscal_w::text, '') = '') and (nr_seq_conta_origem_w IS NOT NULL AND nr_seq_conta_origem_w::text <> '') then 
 
		select	max(b.nr_sequencia) 
		into STRICT	nr_seq_nota_fiscal_w 
		from	nota_fiscal b, 
			conta_paciente a 
		where	coalesce(b.nr_interno_conta::text, '') = '' 
		and	a.nr_seq_protocolo	= b.nr_seq_protocolo 
		and	a.nr_interno_conta	= nr_seq_conta_origem_w;
 
	end if;
 
end if;
 
return	nr_seq_nota_fiscal_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
