SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_nf_conta ( nr_interno_conta_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


/*	1 - retorna nr_sequencia
	2 - retorna nr_nota_fiscal	
	3 - retorna a nota do protocolo
	4 - retorna a nr_sequencia do protocolo
	5 - retorna nr_nota_fiscal da conta de origem caso nao exista uma nf na conta do parametro
	6 - retorna nr_sequencia da conta de origem caso nao exista uma nf na conta do parametro
	7 - retorna o numero de sequencia da nota fiscal (emitida pela conta ou pelo protocolo)
	8 - retorna o nr_sequencia da nota fiscal da conta de origem. 
		caso a conta passada no parametro seja a nova conta gerada nao cancelada, nao ira retornar a nf de origem (os 109753)
	9 - retorna o nr_nota_fiscal da nota fiscal da conta de origem. 
		caso a conta passada no parametro seja a nova conta gerada nao cancelada, nao ira retornar a nf de origem (os 109753)
	10 - Retorna o numero da nota fiscal do titulo	
	11 - Retorna o numero da nota fiscal coletiva
	12 - Retorna o cd_serie_nf + nr_nota_fiscal	
*/
nr_sequencia_w		bigint;
nr_nota_fiscal_w		varchar(255);
ds_retorno_w		varchar(100);
nr_seq_protocolo_w	bigint;
nr_seq_conta_origem_w	bigint;
ie_cancelamento_w		varchar(100);
nr_seq_rule_w           protocolo_convenio.nr_seq_rule%type;
cd_serie_nf_w       nota_fiscal.cd_serie_nf%type;


BEGIN

begin
select	max(nr_sequencia),
        max(somente_numero(nr_nota_fiscal)),
        max(cd_serie_nf)
into STRICT	nr_sequencia_w,
        nr_nota_fiscal_w,
        cd_serie_nf_w
from	nota_fiscal
where	nr_interno_conta = nr_interno_conta_p;
exception
	when no_data_found then
		nr_sequencia_w := null;
end;

ds_retorno_w := null;

/* marcus em 10/01/2008 */

if (ie_opcao_p = 7) then
	begin
	if (coalesce(nr_sequencia_w::text, '') = '') then
		select	max(nr_sequencia)
		into STRICT	nr_sequencia_w
		from	nota_fiscal b,
			conta_paciente a
		where	a.nr_interno_conta	= nr_interno_conta_p
		and	a.nr_seq_protocolo	= b.nr_seq_protocolo
		and	coalesce(b.nr_interno_conta::text, '') = '';
	end if;
	ds_retorno_w		:= to_char(nr_sequencia_w);
	end;
elsif (ie_opcao_p = 1)	then
	ds_retorno_w		:= to_char(nr_sequencia_w);
elsif (ie_opcao_p = 2)	then
	ds_retorno_w		:= substr(nr_nota_fiscal_w,1,100);
elsif (ie_opcao_p = 12)   then
    ds_retorno_w        := CONCAT(substr(cd_serie_nf_w,1,100),substr(nr_nota_fiscal_w,1,100));
elsif (ie_opcao_p = 11) then
  begin
    select max(p.nr_seq_rule)
    into STRICT nr_seq_rule_w
    from protocolo_convenio p,
    conta_paciente a
    where	a.nr_interno_conta	= nr_interno_conta_p
    and a.nr_seq_protocolo	= p.nr_seq_protocolo;
  exception when no_data_found then
    nr_seq_rule_w := 0;
  end;
  if (nr_seq_rule_w > 0) then
    begin
      if (coalesce(nr_sequencia_w::text, '') = '') then
        select	max(b.nr_sequencia)
        into STRICT	nr_sequencia_w
        from	nota_fiscal b,
          conta_paciente a
        where	a.nr_interno_conta	= nr_interno_conta_p
        and	a.nr_seq_protocolo	= b.nr_seq_protocolo
        and	coalesce(b.nr_interno_conta::text, '') = '';
      end if;
      ds_retorno_w		:= to_char(nr_sequencia_w);
    end;
  else
    begin
		select	coalesce(max(b.nr_seq_protocolo),0)
		into STRICT	nr_seq_protocolo_w
		FROM conta_paciente a
LEFT OUTER JOIN protocolo_convenio b ON (a.nr_seq_protocolo = b.nr_seq_protocolo)
WHERE a.nr_interno_conta	= nr_interno_conta_p;
      exception
        when no_data_found then
          nr_sequencia_w := null;
	  end;	
	  
	if (nr_seq_protocolo_w > 0) then
	 	select	coalesce(max(somente_numero(nr_nota_fiscal)),'0')		
		into STRICT	nr_nota_fiscal_w
		from	nota_fiscal
		where	nr_seq_protocolo = nr_seq_protocolo_w
		and	coalesce(nr_interno_conta::text, '') = '';
	else		
		ds_retorno_w		:= substr(nr_nota_fiscal_w,1,100);
	end if;
  end if;
elsif (ie_opcao_p in (3,4,5,6,8,9,10))	then
	begin
	select	coalesce(max(b.nr_seq_protocolo),0),
		max(a.nr_seq_conta_origem),
		max(a.ie_cancelamento)
	into STRICT	nr_seq_protocolo_w,
		nr_seq_conta_origem_w,
		ie_cancelamento_w
	FROM conta_paciente a
LEFT OUTER JOIN protocolo_convenio b ON (a.nr_seq_protocolo = b.nr_seq_protocolo)
WHERE a.nr_interno_conta	= nr_interno_conta_p;

	nr_nota_fiscal_w	:= null;

	if (nr_seq_protocolo_w > 0) then
		select	coalesce(max(somente_numero(nr_nota_fiscal)),'0'),
			coalesce(max(nr_sequencia),0)
		into STRICT	nr_nota_fiscal_w,
			nr_sequencia_w
		from	nota_fiscal
		where	nr_seq_protocolo = nr_seq_protocolo_w
		and	coalesce(nr_interno_conta::text, '') = '';
	end if;

	if (coalesce(nr_nota_fiscal_w,'0') <> '0') and (ie_opcao_p = 3) then
		ds_retorno_w	:= substr(nr_nota_fiscal_w,1,100);
	end if;

	if (coalesce(nr_sequencia_w,0) > 0) and (ie_opcao_p = 4) then
		ds_retorno_w	:= nr_sequencia_w;
	end if;

	if (coalesce(nr_sequencia_w,0) > 0) and (ie_opcao_p = 8) then
		ds_retorno_w	:= nr_sequencia_w;
	end if;

	if (coalesce(nr_sequencia_w,0) > 0) and (ie_opcao_p = 9) then
		ds_retorno_w	:= substr(nr_nota_fiscal_w,1,100);
	end if;

	if (ie_opcao_p in (5,6,8,9)) and (coalesce(ds_retorno_w,0) = 0) and (nr_seq_conta_origem_w IS NOT NULL AND nr_seq_conta_origem_w::text <> '') then

		select	max(somente_numero(nr_nota_fiscal)),
			max(nr_sequencia)
		into STRICT	nr_nota_fiscal_w,
			nr_sequencia_w
		from	nota_fiscal
		where	nr_interno_conta = nr_seq_conta_origem_w;

		if (coalesce(nr_sequencia_w::text, '') = '') then
			select	max(nr_seq_protocolo)
			into STRICT	nr_seq_protocolo_w
			from	conta_paciente
			where	nr_interno_conta = nr_seq_conta_origem_w;

			if (nr_seq_protocolo_w > 0) then

				select	coalesce(max(somente_numero(nr_nota_fiscal)),'0'),
					coalesce(max(nr_sequencia),0)
				into STRICT	nr_nota_fiscal_w,
					nr_sequencia_w
				from	nota_fiscal
				where	nr_seq_protocolo = nr_seq_protocolo_w
				and	coalesce(nr_interno_conta::text, '') = '';
			end if;
		end if;

		if (ie_opcao_p = 5) then
			ds_retorno_w	:= substr(nr_nota_fiscal_w,1,100);
		elsif (ie_opcao_p = 6) then
			ds_retorno_w	:= nr_sequencia_w;
		elsif (ie_opcao_p = 8) then
			if (ie_cancelamento_w IS NOT NULL AND ie_cancelamento_w::text <> '') then
				ds_retorno_w	:= nr_sequencia_w;
			end if;
		elsif (ie_opcao_p = 9) then
			if (ie_cancelamento_w IS NOT NULL AND ie_cancelamento_w::text <> '') then
				ds_retorno_w	:= substr(nr_nota_fiscal_w,1,100);
			end if;
		end if;
	end if;
	if (ie_opcao_p = 10) then

		if (nr_seq_protocolo_w <> 0) then

			select	max(b.nr_nota_fiscal)
			into STRICT	nr_nota_fiscal_w
			from	nota_fiscal b,
				titulo_receber a
			where	a.nr_seq_nf_saida	= b.nr_sequencia
			and	a.nr_seq_protocolo	= nr_seq_protocolo_w;
		end if;

	if (nr_interno_conta_p <> 0) then
			
			select	max(b.nr_nota_fiscal)
			into STRICT	nr_nota_fiscal_w
			from	nota_fiscal b,
				titulo_receber a
			where	a.nr_seq_nf_saida	= b.nr_sequencia
			and	a.nr_interno_conta	= nr_interno_conta_p;

		end if;

		ds_retorno_w	:= substr(nr_nota_fiscal_w,1,100);

	end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_nf_conta ( nr_interno_conta_p bigint, ie_opcao_p bigint) FROM PUBLIC;

