CREATE OR REPLACE FUNCTION obter_nf_estorno_periodo (nr_sequencia_p bigint, dt_inicio_p timestamp, dt_final_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_gerar_nota_w		varchar(2);
qt_nota_w		bigint;


BEGIN

select  count(*)
into STRICT	qt_nota_w
from 	nota_fiscal y
where	y.nr_sequencia = nr_sequencia_p
and	   exists (SELECT 1
			   from nota_fiscal x
			   where x.nr_sequencia_ref = y.nr_sequencia
			   and x.dt_emissao between dt_inicio_p and fim_dia(dt_final_p));

if (qt_nota_w = 0) then
	begin
	select 	count(*)
	into STRICT	qt_nota_w
	from 	nota_fiscal y
	where   y.nr_sequencia 	= nr_sequencia_p
	and	y.ie_situacao 	= 2
	and	exists (SELECT 1
			from	nota_fiscal x
			where	x.nr_sequencia 	  = y.nr_sequencia_ref
			and	coalesce(x.ie_status_envio,'X') <> 'E');
	end;
end if;


if (qt_nota_w > 0) then
	ie_gerar_nota_w := 'N';
else
	ie_gerar_nota_w := 'S';
end if;

return	ie_gerar_nota_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
