CREATE OR REPLACE FUNCTION obter_nf_item_transf_estoque (nr_ordem_compra_p bigint, nr_item_oci_p bigint, ie_entrada_saida_p text) RETURNS varchar AS $body$
DECLARE



nr_nota_fiscal_w	varchar(255);
nr_nota_fiscal_ww	numeric(38);
vl_retorno_w	varchar(255);

c01 CURSOR FOR
SELECT	trim(both a.nr_nota_fiscal),
	somente_numero(a.nr_nota_fiscal)
from	nota_fiscal a,
	nota_fiscal_item b
where	a.nr_sequencia = b.nr_sequencia
and	a.ie_situacao = '1'
and	substr(obter_se_nota_entrada_saida(a.nr_sequencia),1,1) = ie_entrada_saida_p
and	b.nr_item_oci = coalesce(nr_item_oci_p,b.nr_item_oci)
and	b.nr_ordem_compra = nr_ordem_compra_p
group by trim(both a.nr_nota_fiscal),
	somente_numero(a.nr_nota_fiscal)
order by somente_numero(a.nr_nota_fiscal);


BEGIN
begin
open c01;
loop
fetch c01 into
	nr_nota_fiscal_w,
	nr_nota_fiscal_ww;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if (coalesce(vl_retorno_w::text, '') = '') then
		vl_retorno_w := substr(nr_nota_fiscal_w,1,255);
	else
		vl_retorno_w := substr(vl_retorno_w || ', ' || nr_nota_fiscal_w,1,255);
	end if;
	end;
end loop;
close c01;
exception
when others then
	vl_retorno_w := null;
end;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
