CREATE OR REPLACE FUNCTION obter_nf_lote_protocolo (nr_seq_lote_protocolo_p bigint) RETURNS varchar AS $body$
DECLARE


nr_nota_w		varchar(255);
ds_nota_w		varchar(4000) := ' ';


C01 CURSOR FOR
SELECT	nr_nota_fiscal
from	nota_fiscal
where (nr_seq_lote_protocolo_p  <> 0)
and	ie_situacao = '1'
and	nr_seq_lote_prot = nr_seq_lote_protocolo_p
order by 1;


BEGIN

open C01;
loop
fetch C01 into nr_nota_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	if (length(ds_nota_w) < 3600) then
		ds_nota_w := substr(ds_nota_w || nr_nota_w ||', ',1,4000);
	end if;
end loop;
close C01;

RETURN substr(ds_nota_w,1,length(ds_nota_w) - 2);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
