CREATE OR REPLACE FUNCTION obter_nf_mat_atend ( nr_atendimento_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE



NR_NOTA_FISCAL_W		varchar(255);
ds_retorno_w			varchar(255);
C01 CURSOR FOR
	SELECT	b.nr_nota_fiscal
	from	nota_fiscal b,
		nota_fiscal_item a
	where	a.nr_atendimento	= nr_atendimento_p
	and	a.cd_material		= cd_material_p
	and	a.nr_sequencia	= b.nr_sequencia;


BEGIN

OPEN C01;
LOOP
FETCH C01 into
	nr_nota_fiscal_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	if (coalesce(ds_retorno_w::text, '') = '') then
		ds_retorno_w		:= nr_nota_fiscal_w;
	elsif (length(ds_retorno_w) < 180) then
		ds_retorno_w		:= substr(ds_retorno_w || ', ' || nr_nota_fiscal_w,1,255);
	end if;
END LOOP;
CLOSE C01;

return	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
