SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_nf_protocolo (nr_seq_protocolo_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


nr_nota_w		varchar(12);
dt_emissao_w		timestamp;
dt_vencimento_w		timestamp;
nr_sequencia_w		nota_fiscal.nr_sequencia%type;


C01 CURSOR FOR
	SELECT  a.nr_nota_fiscal,
		a.dt_emissao,
		b.dt_vencimento,
		a.nr_sequencia
	from 	nota_fiscal a,
		nota_fiscal_venc b
	where	a.nr_sequencia = b.nr_sequencia
	and	nr_seq_protocolo =  nr_seq_protocolo_p;


BEGIN

open C01;
loop
fetch C01 into
	nr_nota_w,
	dt_emissao_w,
	dt_vencimento_w,
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	nr_nota_w		:= nr_nota_w;
	dt_emissao_w		:= dt_emissao_w;
	dt_vencimento_w 	:= dt_vencimento_w;
	nr_sequencia_w		:= nr_sequencia_w;
	end;
end loop;
close C01;

if (ie_opcao_p = '1') then
	RETURN nr_nota_w;
elsif (ie_opcao_p = '2') then
	RETURN to_char(dt_emissao_w,'ddmmyyyy');
elsif (ie_opcao_p = '3') then
	RETURN to_char(dt_vencimento_w,'ddmmyyyy');
elsif (ie_opcao_p = '4') then
	RETURN nr_sequencia_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
