CREATE OR REPLACE FUNCTION obter_nf_relat_compras ( nr_ordem_compra_p bigint, nr_item_oci_p bigint) RETURNS bigint AS $body$
DECLARE


nr_sequencia_w		nota_fiscal.nr_sequencia%type;

c01 CURSOR FOR
SELECT	a.nr_sequencia
from	nota_fiscal a,
	nota_fiscal_item b
where	a.nr_sequencia = b.nr_sequencia
and	b.nr_ordem_compra = nr_ordem_compra_p
and	b.nr_item_oci = nr_item_oci_p
and	a.ie_situacao = '1'
order by nr_sequencia desc;


BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	nr_sequencia_w := nr_sequencia_w;
	exit;
	end;
end loop;
close C01;

return	nr_sequencia_w;

end;
$body$
LANGUAGE PLPGSQL
