CREATE OR REPLACE FUNCTION obter_nf_repasse (nr_repasse_terceiro_p bigint) RETURNS varchar AS $body$
DECLARE


nr_nota_w		varchar(255);
ds_nota_w		varchar(4000) := ' ';

C01 CURSOR FOR
SELECT	distinct b.nr_nota_fiscal
from	nota_fiscal b,
	repasse_nota_fiscal a
where	a.nr_seq_nota_fiscal	= b.nr_sequencia
and	a.nr_repasse_terCeiro	= nr_repasse_terceiro_p
order	by 1;


BEGIN

open C01;
loop
fetch C01 into
	nr_nota_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	if (length(ds_nota_w) < 3600) then
		ds_nota_w := substr(ds_nota_w || nr_nota_w ||', ',1,4000);
	end if;
end loop;
close C01;

RETURN substr(ds_nota_w,1,length(ds_nota_w) - 2);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
