CREATE OR REPLACE FUNCTION obter_nf_retorno_receb (nr_seq_retorno_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_receb_w		bigint;
nr_nota_fiscal_w	varchar(255);
ds_retorno_w		varchar(2000);

c01 CURSOR FOR
SELECT	nr_seq_receb
from	convenio_ret_receb
where	nr_seq_retorno	= nr_seq_retorno_p
group by nr_seq_receb;

c02 CURSOR FOR
SELECT	b.nr_nota_fiscal
from	convenio_receb_nf a,
	nota_fiscal b
where	a.NR_SEQ_NOTA_FISCAL	= b.nr_sequencia
and	a.nr_seq_receb		= nr_seq_receb_w
and	b.ie_situacao 		= '1'
group by b.nr_nota_fiscal;


BEGIN

ds_retorno_w	:= null;

open C01;
loop
fetch C01 into
	nr_seq_receb_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	open C02;
	loop
	fetch C02 into
		nr_nota_fiscal_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

		if (coalesce(ds_retorno_w::text, '') = '') then
			ds_retorno_w	:= nr_nota_fiscal_w;
		else
			ds_retorno_w	:= ds_retorno_w ||', '||nr_nota_fiscal_w;
		end if;

		end;
	end loop;
	close C02;

	end;
end loop;
close C01;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
