SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_nf_titulos_convenio_rec ( nr_seq_receb_p bigint) RETURNS varchar AS $body$
DECLARE

 
nr_nota_fiscal_w			varchar(255);
ds_notas_fiscais_w			varchar(255);
ie_contido_w			varchar(001);

c01 CURSOR FOR 
SELECT	substr(obter_nf_conta_ret(b.nr_interno_conta),1,255) 
from	conta_paciente b, 
	convenio_retorno_item a, 
	convenio_ret_receb d 
where	a.nr_interno_conta	= b.nr_interno_conta 
and	a.nr_seq_retorno	= d.nr_seq_retorno 
and	d.nr_seq_receb		= nr_seq_receb_p 
order	by 1;


BEGIN 
 
ds_notas_fiscais_w	:= '';
 
open c01;
loop 
fetch c01 into 
	nr_nota_fiscal_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	if (ds_notas_fiscais_w IS NOT NULL AND ds_notas_fiscais_w::text <> '') then 
		begin 
		ie_contido_w		:= substr(ctb_obter_se_elemento_contido(nr_nota_fiscal_w, ds_notas_fiscais_w),1,1);
		if (ie_contido_w <> 'S') then 
			ds_notas_fiscais_w	:= substr(ds_notas_fiscais_w || ', ' || nr_nota_fiscal_w, 1, 255);
		end if;
		end;
	else 
		ds_notas_fiscais_w	:= nr_nota_fiscal_w;
	end if;
end loop;
close c01;
 
return	ds_notas_fiscais_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
