CREATE OR REPLACE FUNCTION obter_nf_ultima_compra_data ( cd_material_p bigint, cd_estabelecimento_p bigint, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(14) := '';
nr_seq_ultima_compra_w		bigint;


BEGIN

select	a.nr_nota_fiscal
into STRICT	ds_retorno_w
from	nota_fiscal a
where	a.nr_sequencia = (
	SELECT	max(b.nr_sequencia)
	from	natureza_operacao o,
		operacao_nota p,
		nota_fiscal n,
		nota_fiscal_item b
	where	b.nr_sequencia		= n.nr_sequencia
	and	n.cd_natureza_operacao	= o.cd_natureza_operacao
	and	b.cd_material		= cd_material_p
	and	n.cd_operacao_nf 		= p.cd_operacao_nf
	and	coalesce(p.ie_ultima_compra, 'S') 	= 'S'
	and	n.cd_estabelecimento	= cd_estabelecimento_p
	and	o.ie_entrada_saida		= 'E'
	and	n.ie_acao_nf		= '1'
	and	n.dt_atualizacao		<= dt_referencia_p
	and	n.ie_situacao		= '1');

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
